// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/governance/utils/IVotes.sol";

/**
 * @title SecureGovernance
 * @notice SECURE VERSION - All vulnerabilities fixed
 * @dev Fixes applied:
 * 1. Vote weight snapshots to prevent flash loan attacks
 * 2. Timelock delay before execution
 * 3. Proper quorum requirements
 * 4. Access controls on admin functions
 * 5. Safe delegation mechanism
 */
contract SecureGovernance {
    IVotes public immutable governanceToken;

    struct Proposal {
        address proposer;
        address target;
        bytes data;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 startBlock;
        uint256 endBlock;
        uint256 eta; // FIX 2: Execution timelock
        bool executed;
        bool canceled;
        mapping(address => bool) hasVoted;
    }

    mapping(uint256 => Proposal) public proposals;
    uint256 public proposalCount;

    uint256 public votingDelay = 1; // 1 block delay before voting starts
    uint256 public votingPeriod = 45818; // ~1 week
    uint256 public proposalThreshold = 100000e18; // 100k tokens
    uint256 public quorumVotes = 400000e18; // FIX 3: 400k quorum
    uint256 public constant TIMELOCK_DELAY = 2 days; // FIX 2: 2 day timelock

    address public admin;
    address public pendingAdmin;

    event ProposalCreated(
        uint256 indexed proposalId,
        address proposer,
        address target,
        uint256 startBlock,
        uint256 endBlock
    );
    event VoteCast(
        address indexed voter,
        uint256 indexed proposalId,
        bool support,
        uint256 votes
    );
    event ProposalQueued(uint256 indexed proposalId, uint256 eta);
    event ProposalExecuted(uint256 indexed proposalId);
    event ProposalCanceled(uint256 indexed proposalId);

    modifier onlyAdmin() {
        require(msg.sender == admin, "Not admin");
        _;
    }

    constructor(address _governanceToken) {
        governanceToken = IVotes(_governanceToken);
        admin = msg.sender;
    }

    /**
     * @notice FIX 1: Uses token's getVotes with snapshot
     * @dev Requires token to implement IVotes (EIP-5805)
     */
    function propose(address target, bytes memory data) external returns (uint256) {
        // FIX 1: Check voting power at previous block (snapshot)
        require(
            governanceToken.getVotes(msg.sender) >= proposalThreshold,
            "Below proposal threshold"
        );

        uint256 proposalId = proposalCount++;
        Proposal storage proposal = proposals[proposalId];

        proposal.proposer = msg.sender;
        proposal.target = target;
        proposal.data = data;
        proposal.startBlock = block.number + votingDelay; // FIX: Delay before voting
        proposal.endBlock = block.number + votingDelay + votingPeriod;

        emit ProposalCreated(
            proposalId,
            msg.sender,
            target,
            proposal.startBlock,
            proposal.endBlock
        );

        return proposalId;
    }

    /**
     * @notice FIX 1: Uses snapshot voting power
     * @dev Votes based on power at proposal start, not current balance
     */
    function castVote(uint256 proposalId, bool support) external {
        Proposal storage proposal = proposals[proposalId];

        require(block.number >= proposal.startBlock, "Voting not started");
        require(block.number <= proposal.endBlock, "Voting ended");
        require(!proposal.hasVoted[msg.sender], "Already voted");

        // FIX 1: Get voting power from snapshot at proposal start block
        uint256 votes = governanceToken.getPastVotes(
            msg.sender,
            proposal.startBlock - 1
        );
        require(votes > 0, "No voting power");

        proposal.hasVoted[msg.sender] = true;

        if (support) {
            proposal.forVotes += votes;
        } else {
            proposal.againstVotes += votes;
        }

        emit VoteCast(msg.sender, proposalId, support, votes);
    }

    /**
     * @notice FIX 2: Queue proposal with timelock
     * @notice FIX 3: Verify quorum requirement
     */
    function queue(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];

        require(block.number > proposal.endBlock, "Voting not ended");
        require(!proposal.executed, "Already executed");
        require(!proposal.canceled, "Proposal canceled");
        require(proposal.eta == 0, "Already queued");

        // FIX 3: Verify quorum met
        uint256 totalVotes = proposal.forVotes + proposal.againstVotes;
        require(totalVotes >= quorumVotes, "Quorum not reached");

        // FIX 3: Verify proposal passed
        require(proposal.forVotes > proposal.againstVotes, "Proposal failed");

        // FIX 2: Set timelock delay
        proposal.eta = block.timestamp + TIMELOCK_DELAY;

        emit ProposalQueued(proposalId, proposal.eta);
    }

    /**
     * @notice FIX 2: Can only execute after timelock
     */
    function execute(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];

        require(proposal.eta != 0, "Not queued");
        require(!proposal.executed, "Already executed");
        require(!proposal.canceled, "Proposal canceled");

        // FIX 2: Enforce timelock delay
        require(block.timestamp >= proposal.eta, "Timelock not met");
        require(
            block.timestamp <= proposal.eta + 14 days,
            "Execution window expired"
        );

        proposal.executed = true;

        (bool success, bytes memory returnData) = proposal.target.call(proposal.data);
        require(success, string(returnData));

        emit ProposalExecuted(proposalId);
    }

    /**
     * @notice FIX 4: Only proposer or admin can cancel
     */
    function cancel(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];

        require(!proposal.executed, "Already executed");
        require(!proposal.canceled, "Already canceled");

        // FIX 4: Access control - only proposer or admin
        require(
            msg.sender == proposal.proposer || msg.sender == admin,
            "Not authorized"
        );

        // Additional check: Can cancel if proposer drops below threshold
        if (msg.sender == proposal.proposer) {
            require(
                governanceToken.getVotes(proposal.proposer) < proposalThreshold,
                "Proposer still has voting power"
            );
        }

        proposal.canceled = true;
        emit ProposalCanceled(proposalId);
    }

    /**
     * @notice FIX 4: Admin functions with access control
     */
    function setVotingDelay(uint256 newDelay) external onlyAdmin {
        require(newDelay >= 1, "Delay too short");
        votingDelay = newDelay;
    }

    function setVotingPeriod(uint256 newPeriod) external onlyAdmin {
        require(newPeriod >= 5760, "Period too short"); // ~1 day minimum
        votingPeriod = newPeriod;
    }

    function setProposalThreshold(uint256 newThreshold) external onlyAdmin {
        require(newThreshold > 0, "Threshold too low");
        proposalThreshold = newThreshold;
    }

    function setQuorum(uint256 newQuorum) external onlyAdmin {
        require(newQuorum > 0, "Quorum too low");
        quorumVotes = newQuorum;
    }

    /**
     * @notice Two-step admin transfer for safety
     */
    function setPendingAdmin(address newAdmin) external onlyAdmin {
        require(newAdmin != address(0), "Invalid address");
        pendingAdmin = newAdmin;
    }

    function acceptAdmin() external {
        require(msg.sender == pendingAdmin, "Not pending admin");
        admin = pendingAdmin;
        pendingAdmin = address(0);
    }

    /**
     * @notice View functions for proposal state
     */
    function state(uint256 proposalId) public view returns (uint8) {
        Proposal storage proposal = proposals[proposalId];

        if (proposal.canceled) return 2; // Canceled
        if (proposal.executed) return 7; // Executed
        if (block.number <= proposal.startBlock) return 0; // Pending
        if (block.number <= proposal.endBlock) return 1; // Active

        uint256 totalVotes = proposal.forVotes + proposal.againstVotes;
        if (totalVotes < quorumVotes) return 3; // Defeated - no quorum
        if (proposal.forVotes <= proposal.againstVotes) return 3; // Defeated
        if (proposal.eta == 0) return 4; // Succeeded - not queued
        if (block.timestamp < proposal.eta) return 5; // Queued
        if (block.timestamp > proposal.eta + 14 days) return 6; // Expired

        return 5; // Queued - ready to execute
    }
}

src/FixedContracts/SecureRewardDistribut.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SecureRewardsDistributor
 * @notice SECURE VERSION - All vulnerabilities fixed
 */
contract SecureRewardsDistributor is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    /* State Variables */
    IERC20 public immutable rewardToken;
    IERC20 public immutable stakingToken;

    uint256 public rewardRate;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public periodFinish;
    uint256 public rewardsDuration = 7 days;
    uint256 public totalStaked;

    // FIX 5: Minimum stake period to prevent flash loan attacks
    uint256 public constant MINIMUM_STAKE_PERIOD = 1; // 1 block minimum

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(address => uint256) public balances;
    mapping(address => uint256) public stakeTime;

    /* Events */
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event RewardAdded(uint256 reward);
    event RewardsDurationUpdated(uint256 newDuration);

    constructor(
        address _stakingToken,
        address _rewardToken,
        uint256 _rewardRate
    ) Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
        rewardRate = _rewardRate;
        lastUpdateTime = block.timestamp;
    }

    /* Views */
    function lastTimeRewardApplicable() public view returns (uint256) {
        return block.timestamp < periodFinish ? block.timestamp : periodFinish;
    }

    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }
        return rewardPerTokenStored +
            (((lastTimeRewardApplicable() - lastUpdateTime) * rewardRate * 1e18) / totalStaked);
    }

    function earned(address account) public view returns (uint256) {
        return (balances[account] *
            (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18 +
            rewards[account];
    }

    /* Modifiers */
    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();

        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /* User Functions */

    /**
     * @notice Stakes tokens to earn rewards
     */
    function stake(uint256 amount) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot stake 0");

        totalStaked += amount;
        balances[msg.sender] += amount;
        stakeTime[msg.sender] = block.number;

        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
    }

    /**
     * @notice Withdraws staked tokens
     */
    function withdraw(uint256 amount) public nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot withdraw 0");
        require(balances[msg.sender] >= amount, "Insufficient balance");
        require(
            block.number >= stakeTime[msg.sender] + MINIMUM_STAKE_PERIOD,
            "Minimum stake period not met"
        );

        balances[msg.sender] -= amount;
        totalStaked -= amount;

        stakingToken.safeTransfer(msg.sender, amount);
        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @notice Claims earned rewards
     */
    function claim() public nonReentrant updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        require(reward > 0, "No rewards");
        require(
            block.number >= stakeTime[msg.sender] + MINIMUM_STAKE_PERIOD,
            "Minimum stake period not met"
        );

        rewards[msg.sender] = 0;
        rewardToken.safeTransfer(msg.sender, reward);
        emit RewardPaid(msg.sender, reward);
    }

    /**
     * @notice Combined withdraw and claim
     */
    function exit() external {
        withdraw(balances[msg.sender]);
        claim();
    }

    /* Admin Functions */

    function setRewardRate(uint256 newRate) external onlyOwner updateReward(address(0)) {
        require(newRate > 0, "Rate must be positive");
        rewardRate = newRate;
    }

    function notifyRewardAmount(uint256 reward) external onlyOwner updateReward(address(0)) {
        require(reward > 0, "Reward must be positive");

        if (block.timestamp >= periodFinish) {
            rewardRate = reward / rewardsDuration;
        } else {
            uint256 remaining = periodFinish - block.timestamp;
            uint256 leftover = remaining * rewardRate;
            rewardRate = (reward + leftover) / rewardsDuration;
        }

        require(rewardRate > 0, "Reward rate too low");
        require(
            rewardRate <= rewardToken.balanceOf(address(this)) / rewardsDuration,
            "Insufficient reward balance"
        );

        lastUpdateTime = block.timestamp;
        periodFinish = block.timestamp + rewardsDuration;

        emit RewardAdded(reward);
    }

    function setRewardsDuration(uint256 duration) external onlyOwner {
        require(block.timestamp > periodFinish, "Previous period must be complete");
        require(duration > 0, "Duration must be positive");

        rewardsDuration = duration;
        emit RewardsDurationUpdated(duration);
    }

    function emergencyWithdrawRewards(address token, uint256 amount) external onlyOwner {
        require(block.timestamp > periodFinish + 30 days, "Must wait 30 days after period end");
        IERC20(token).safeTransfer(owner(), amount);
    }
}

src/FixedContracts/SecureVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SecureVault
 * @notice SECURE VERSION - All vulnerabilities fixed
 * @dev Fixes applied:
 * 1. ReentrancyGuard for all external calls
 * 2. Virtual shares to prevent first depositor attack
 * 3. Higher precision arithmetic
 * 4. Proper access controls with Ownable
 * 5. Removed donation function, proper fee handling
 */
contract SecureVault is ERC20, ReentrancyGuard, Ownable {
    IERC20 public immutable asset;

    uint256 private constant VIRTUAL_SHARES = 1e3;
    uint256 private constant VIRTUAL_ASSETS = 1;
    uint256 private constant FEE_DENOMINATOR = 10000;
    uint256 private constant MAX_FEE = 1000; // 10% max

    uint256 public performanceFee = 100; // 1%
    uint256 public totalAssets;
    address public feeRecipient;

    event Deposit(address indexed sender, uint256 assets, uint256 shares);
    event Withdraw(address indexed sender, uint256 assets, uint256 shares);
    event FeesCollected(address indexed recipient, uint256 amount);
    event FeeRecipientUpdated(address indexed newRecipient);
    event PerformanceFeeUpdated(uint256 newFee);

    constructor(
        address _asset,
        string memory _name,
        string memory _symbol,
        address _feeRecipient
    ) ERC20(_name, _symbol) Ownable(msg.sender) {
        asset = IERC20(_asset);
        feeRecipient = _feeRecipient;
    }

    /**
     * @notice FIX 1: ReentrancyGuard prevents reentrancy
     * @notice FIX 2: Check-Effects-Interactions pattern
     */
    function deposit(uint256 assets) external nonReentrant returns (uint256 shares) {
        require(assets > 0, "Cannot deposit 0");

        // Calculate shares BEFORE any state changes
        shares = convertToShares(assets);
        require(shares > 0, "Cannot mint 0 shares");

        // Effects: Update state BEFORE external calls
        totalAssets += assets;
        _mint(msg.sender, shares);

        // Interactions: External calls LAST
        require(
            asset.transferFrom(msg.sender, address(this), assets),
            "Transfer failed"
        );

        emit Deposit(msg.sender, assets, shares);
    }

    /**
     * @notice FIX 1: ReentrancyGuard + Check-Effects-Interactions
     */
    function withdraw(uint256 shares) external nonReentrant returns (uint256 assets) {
        require(shares > 0, "Cannot withdraw 0");
        require(balanceOf(msg.sender) >= shares, "Insufficient shares");

        // Calculate assets BEFORE state changes
        assets = convertToAssets(shares);
        require(assets > 0, "Cannot withdraw 0 assets");

        // Effects: Update state BEFORE external calls
        _burn(msg.sender, shares);
        totalAssets -= assets;

        // Interactions: External call LAST
        require(asset.transfer(msg.sender, assets), "Transfer failed");

        emit Withdraw(msg.sender, assets, shares);
    }

    /**
     * @notice FIX 2: Virtual shares prevent first depositor attack
     * @notice FIX 3: Higher precision calculation
     */
    function convertToShares(uint256 assets) public view returns (uint256) {
        uint256 supply = totalSupply();

        // Use virtual shares to prevent first depositor attack
        return (assets * (supply + VIRTUAL_SHARES)) / (totalAssets + VIRTUAL_ASSETS);
    }

    /**
     * @notice FIX 3: Improved precision
     */
    function convertToAssets(uint256 shares) public view returns (uint256) {
        uint256 supply = totalSupply();

        // Use virtual shares for consistency
        return (shares * (totalAssets + VIRTUAL_ASSETS)) / (supply + VIRTUAL_SHARES);
    }

    /**
     * @notice FIX 4: Proper access control with onlyOwner
     */
    function collectFees() external onlyOwner nonReentrant {
        require(feeRecipient != address(0), "Fee recipient not set");

        uint256 feeAmount = (totalAssets * performanceFee) / FEE_DENOMINATOR;
        require(feeAmount > 0, "No fees to collect");

        // Effects before interactions
        totalAssets -= feeAmount;

        // Interactions last
        require(asset.transfer(feeRecipient, feeAmount), "Fee transfer failed");

        emit FeesCollected(feeRecipient, feeAmount);
    }

    /**
     * @notice FIX 4: Access control + validation
     */
    function setPerformanceFee(uint256 newFee) external onlyOwner {
        require(newFee <= MAX_FEE, "Fee exceeds maximum");
        performanceFee = newFee;
        emit PerformanceFeeUpdated(newFee);
    }

    /**
     * @notice FIX 4: Access control for fee recipient
     */
    function setFeeRecipient(address newRecipient) external onlyOwner {
        require(newRecipient != address(0), "Invalid recipient");
        feeRecipient = newRecipient;
        emit FeeRecipientUpdated(newRecipient);
    }

    /**
     * @notice FIX 5: Removed donation function entirely
     * @dev No way to manipulate share price via donations
     */

    /**
     * @notice FIX 4: Proper access control for emergency functions
     */
    function emergencyWithdraw(address recipient) external onlyOwner nonReentrant {
        require(recipient != address(0), "Invalid recipient");

        uint256 balance = asset.balanceOf(address(this));
        totalAssets = 0;

        require(asset.transfer(recipient, balance), "Transfer failed");
    }

    /**
     * @notice View function to check vault health
     */
    function maxDeposit(address) public pure returns (uint256) {
        return type(uint256).max;
    }

    /**
     * @notice View function for max withdrawal
     */
    function maxWithdraw(address owner) public view returns (uint256) {
        return convertToAssets(balanceOf(owner));
    }
}

